#import libraries
import pymysql
from sqlalchemy import create_engine

#database credentials
host=host
username=username
password=password
db='dermstore_data_challenge'

#connect to database
engine = create_engine('mysql+pymysql://'+username+':'+password+'@'+host+'/'+db)
connection = engine.connect()

#5 rows of random customer data
#no need to provide customer id or date_created value. these are auto-generated by table schema
insert = 'INSERT INTO customers (customer_firstname, customer_lastname,customer_email) VALUES(?,?,?)'
