values4 = ('Lisa','Simpson','lisa.simpson@gmail.com')
values5 = ('Maggie','Simpson','maggie.simpson@gmail.com')

#execute insert statements
engine.execute(insert,values1)
engine.execute(insert,values2)
engine.execute(insert,values3)
engine.execute(insert,values4)
engine.execute(insert,values5)

connection.close()